'    l_column_position t_column_position;',
'begin',
'',
'    -- specify columns to select from the web source module',
'    apex_exec.add_column( ',
'        p_columns       => l_columns,',
'        p_column_name   => ''TITLE'' );',
'    apex_exec.add_column( ',
